mapper.update:
UPDATE cart    
SET quantity = #{quantity} 
WHERE user_idx= #{userIdx} 
AND game_idx = #{gameIdx}

mapper.select:
SELECT COUNT(*)
FROM cart
WHERE user_idx= #{userIdx}
AND game_idx = #{gameIdx}

mapper.update:
UPDATE cart 
SET quantity = quantity + #{quantity} 
WHERE user_idx= #{userIdx} 
AND game_idx = #{gameIdx}
