mapper.insert:
        INSERT INTO order_mt (
        	member_idx
          , addr
          , phone
          , payment_type )
        VALUES (
        	#{memberIdx}
		  , #{addr}
		  , #{phone}
		  , #{paymentType} )

    	INSERT INTO order_dt (
    		sno
    	  , game_idx
    	  , quantity )
		VALUES 
		
			#{item.sno}
			, #{item.game_idx}
			, #{item.quantity}

mapper.update:
UPDATE order_mt
SET addr = #{addr}
  , phone = #{phone}
  , payment_type = #{payment_type}
WHERE idx = #{idx}

UPDATE order_dt
SET quantity = #{quantity}
WHERE idx = #{idx}
AND sno = #{sno}

mapper.delete:
        DELETE FROM order_dt
        WHERE idx = #{idx}
		
		AND sno = #{sno}
